mapper.select:
select count(id) from pro_score where 1 = 1

    and year = #{pro.year}



    and province = #{pro.province}



    and type = #{pro.type}



    and rank  like CONCAT('%',#{pro.rank},'%')



    and score > (#{pro.score} - 20) and score < (#{pro.score}  + 20 )

select * from pro_score where 1 = 1

    and year = #{pro.year}



    and province = #{pro.province}



    and type = #{pro.type}



    and rank  like CONCAT('%',#{pro.rank},'%')



    and score > (#{pro.score} - 20) and score < (#{pro.score}  + 20 ) order by score desc



    limit #{pro.offLeft}, #{pro.offRight}
