mapper.select:
select * from schools where 1 = 1

    and sname like CONCAT('%',#{schools.sname},'%')



    and sid = #{schools.sid}



    and provice = #{schools.provice}



    and rank like CONCAT('%',#{schools.rank},'%')



    and type like CONCAT('%',#{schools.type},'%')



    order by ranking ASC limit #{schools.offLeft}, #{schools.offRight}



    order by ranking ASC
